mapper.select:
select articleNo, userId, subject, content, hit, registerTime
from board
where subject like concat('%', #{subtitle},'%');

select articleNo, userId, subject, content, hit, registerTime
from board
where articleNo = #{articleNo}

mapper.update:
update board
set hit = hit + 1
where articleNo = #{articleNo}

update board
set subject = #{subject}, content = #{content}
where articleNo = #{articleNo}
